import { observable, autorun } from 'mobx';
import { appStore } from '../../appStore';
import { NavigationScreenProp } from 'react-navigation';
import { toastHelper } from '../../helpers/toastHelper';
import { GiftedChat } from 'react-native-gifted-chat';

export class ChatStore {

    food: any = {} as any;

    @observable showModal = false;

    @observable text = '';

    @observable toast: any

    @observable messages: any = [];
    @observable typing = false;

    constructor() {
        this.initData();
        autorun(() => {
            setTimeout(() => {
                this.typing = true;
                setTimeout(() => {
                    this.typing = false;
                    this.responseData();
                }, 2200);
            }, 2000);
        });


    }

    initData = async () => {
        const msg = [
            {
                _id: 2,
                text: 'Hello. Can I pick up this one at 6pm?',
                createdAt: new Date(),
                // image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQlMjBmRe276UXTHtwcaKECyv9QxW-ltRSomGngjrjdFq7dwe_A&usqp=CAU',
                user: {
                    _id: 1,
                    name: 'VaNi',
                    avatar: 'data:image/jpeg;base64,',
                },
            },
            {
                _id: 1,
                text: 'France bread',
                createdAt: new Date(),
                image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQlMjBmRe276UXTHtwcaKECyv9QxW-ltRSomGngjrjdFq7dwe_A&usqp=CAU',
                user: {
                    _id: 1,
                    name: 'VaNi',
                    avatar: 'data:image/jpeg;base64,',
                },
            },
        ];
        const newMsg = GiftedChat.append(this.messages, msg);
        this.messages = newMsg;
    }

    responseData = async () => {
        const msg = [
            {
                _id: 2,
                text: 'Ok. Sure',
                createdAt: new Date(),
                // image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQlMjBmRe276UXTHtwcaKECyv9QxW-ltRSomGngjrjdFq7dwe_A&usqp=CAU',
                user: {
                    _id: 2,
                    name: 'VaNi',
                    avatar: 'data:image/jpeg;base64,',
                },
            },
        ];
        const newMsg = GiftedChat.append(this.messages, msg);
        this.messages = newMsg;
    }

    onSend(messages: any[] = []) {
        const msg = GiftedChat.append(this.messages, messages);
        this.messages = msg;
    }

    setState = (txt: any) => {
        this.text = txt;
    }

    onPressSubmit = () => {
        if (this.toast) {
            this.showModal = false;
            this.toast.show('Successfully sent request', 2500);
        }
    }

    onPressRequest = () => {
        this.text = '';
        this.showModal = true;
    }

    onPressCloseRequest = () => {
        this.showModal = false;
    }

    onPressScanButton = () => {
        appStore.isVisibleScannerModal = true;
    }


}

